import React, { Component } from 'react'
import firebase from './firebase'
export class App extends Component {
  handleClick=()=>{
    let recaptcha = new firebase.auth.RecaptchaVerifier('recaptcha');
    let number = '+919875596436';
    firebase.auth().signInWithPhoneNumber(number,recaptcha).then(
      function(e){
        var code = prompt('인증 번호를 입력해주세요', '');


        if(code === null) return;


        e.confirm(code).then(function (result) {
            console.log('인증 성공', result.user);

            document.querySelector('label').textContent += '성공 ' + result.user.phoneNumber;

        }).catch(function (error) {
            console.error('인증 실패', error);

        });

    })
  }
  render() {
    return (
      <div>
        <label></label>
        <button
        onClick={this.handleClick}
        >Click here</button>
      </div>
    )
  }
}

export default App
